package com.farbig.examples.jsf.ee8;import javax.enterprise.context.SessionScoped;import javax.inject.Named;import java.io.Serializable;@Named("commandScriptBean")@SessionScopedpublic class CommandScriptBean implements Serializable {  private String value;  public String getValue() {    return value;  }  public void execute(String input) {    value = input.toUpperCase();  }}