package com.farbig.examples.jsf.ee8;import javax.validation.ConstraintValidator;import javax.validation.ConstraintValidatorContext;public class WholeValidator implements ConstraintValidator<Whole, WholeBean> {  @Override  public void initialize(Whole constraintAnnotation) {  }  @Override  public boolean isValid(WholeBean value, ConstraintValidatorContext context) {    return value.getValue2().equals(value.getValue1());  }}